import React, { useState } from 'react';
import { TextField, Button, FormControl, InputLabel, Select, MenuItem, Grid } from '@mui/material';
import SignupForm from '../signupForm/SignupForm';

const SigninForm = () => {

 const [showSignin, setShowSignin] = useState(true);
 const [showSignup, setShowSignup] = useState(false);

  const [formData, setFormData] = useState({

    email: '',
    password: '',

  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission here
    console.log(formData);
  };

  const toggleSignin = () => {
    setShowSignin(true);
    setShowSignup(false);
  };

  const toggleSignup = () => {
    setShowSignin(false);
    setShowSignup(true);
  };

  return (
    <div>
    {showSignin &&
    <div  style={{ display: "flex", justifyContent: "center", alignItems: "center", height: "100vh" }} >
      <Grid container spacing={2}  style={{ width: "30%", justifyContent: "center"}}>

        <Grid item xs={12} sm={12}>
          <TextField
            fullWidth
            label="Email"
            name="email"
            type="email"
            value={formData.email}
            onChange={handleChange}
          />
        </Grid>


        <Grid item xs={12} sm={12}>
          <TextField
            fullWidth
            label="Password"
            name="password"
            type="password"
            value={formData.password}
            onChange={handleChange}
          />
        </Grid>


        <Grid item xs={12} sm={12}>
          <p>Provide your credential to login</p>
        </Grid>
        <Grid item xs={12} sm={12}>
          <Button type="submit" variant="contained" color="primary" fullWidth= "true" onClick={handleSubmit} >Sign In</Button>
        </Grid>

        <Grid item xs={12} sm={12}>
          <p>If Not registered! Click Sign Up Button</p>
        </Grid>
        <Grid item xs={12} sm={12}>
          <Button type="submit" variant="contained" color="primary" fullWidth= "true" onClick={toggleSignup} >Sign Up</Button>
        </Grid>
      </Grid>
    </div>
    }
    {showSignup && <SignupForm toggleSignin={toggleSignin} />}
    </div>

  );
};

export default SigninForm;
