import React, { useState } from 'react';
import { TextField, Button, FormControl, InputLabel, Select, MenuItem, Grid } from '@mui/material';
import SignupForm from '../signupForm/SignupForm';

const SigninForm = () => {

 const [showSignin, setShowSignin] = useState(true);
 const [showSignUp, setShowSignUp] = useState(false);

  const [formData, setFormData] = useState({

    email: '',
    password: '',

  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission here
    console.log(formData);
  };

  const toggleSignin = () => {
    setShowSignin(true);
    setShowSignUp(false);
  };

  const toggleSignup = () => {
    setShowSignin(false);
    setShowSignUp(true);
  };

  return (
    <div  style={{ display: "flex", justifyContent: "center", alignItems: "center", height: "100vh" }} >
      <Grid container spacing={2}  style={{ width: "30%", justifyContent: "center"}}>

        <Grid item xs={12} sm={12}>
          <TextField
            fullWidth
            label="Email"
            name="email"
            type="email"
            value={formData.email}
            onChange={handleChange}
          />
        </Grid>


        <Grid item xs={12} sm={12}>
          <TextField
            fullWidth
            label="Password"
            name="password"
            type="password"
            value={formData.password}
            onChange={handleChange}
          />
        </Grid>


        <Grid item xs={12} sm={12}>
          <p>Provide your credential to login</p>
        </Grid>
        <Grid item xs={12} sm={12}>
          <Button type="submit" variant="contained" color="primary" fullWidth= "true" onClick={handleSubmit} >Sign In</Button>
        </Grid>
      </Grid>
    </div>
  );
};

export default SigninForm;
